import React from "react";
import "../styles/Auth.css";
import axios from "axios";
import RegisterForm from "./RegisterForm";
import "../styles/GridLayout.css";
import Logo from "../../assets/images/logo.png";
import Item from "../Item";

class AuthContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      user: {},
      content: null
    };
  }

  handleMailChange = e => {
    this.setState({ email: e.target.value });
  };

  handlePasswordChange = e => {
    this.setState({ password: e.target.value });
  };

  handleRegisterUserClick = () => {
    this.setState({ content: <RegisterForm></RegisterForm> });
  };

  loginUser = () => {
    let obj = {
      email: this.state.email,
      password: this.state.password
    };
    let url = "http://localhost:9000/user1/api/login";
    axios
      .post(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Sie wurden erfolgreich eingeloggt.");
    window.location.reload();
  };

  render() {
    return (
      <React.Fragment>
        <div className="navigationBar">
          <header>
            <nav className="navigation">
              <div className="toolbarLogo">
                <img src={Logo} alt="logo" title="logo" height="40px"></img>
              </div>
              <div></div>
            </nav>
          </header>
        </div>
        <div className="authHead">
          <h1>Willkommen bei fily - Rechnungsmanager </h1>
        </div>
        <div className="authContainer">
          <div className="auth">
            <div className="authFormDiv">
              <h2>Melden Sie sich an:</h2>
              <form className="authForm">
                <label>E-Mail: </label>
                <input
                  className="authInput"
                  type="text"
                  onChange={this.handleMailChange}
                  required
                ></input>
                <label>Passwort: </label>
                <input
                  className="authInput"
                  type="password"
                  onChange={this.handlePasswordChange}
                  required
                ></input>
              </form>
              <div className="authButtonDiv">
                <button className="buttonA" onClick={this.loginUser}>
                  Login
                </button>
                <button
                  className="buttonA"
                  onClick={this.handleRegisterUserClick}
                >
                  Registrieren
                </button>
              </div>
            </div>
          </div>
          {this.state.content}
        </div>
        <div className="ImpressumContent">
        <Item className="col-4" header="Unser Standort:" content="">
          <p>Treskowallee 8, 10318 Berlin</p>
          <iframe
            title="frame"
            width="400"
            height="500"
            id="gmap_canvas"
            src="https://maps.google.com/maps?q=treskowallee%208%20berlin&t=&z=13&ie=UTF8&iwloc=&output=embed"
            frameBorder="0"
            scrolling="no"
            marginHeight="0"
            marginWidth="0"
          ></iframe>
        </Item>
        <Item className="col-4" header="Über uns:" content="Webtech Projekt">
          <p>
            Nico Teichert <br />
            Antoine Ma
            <br />
            Kemal Solapgir
            <br />
            Perica Glavas
            <br />
          </p>
        </Item>
        <Item
          className="col-4"
          header="Rechtliche Hinweise:"
          content="Die Inhalte unserer Internetseite werden mit größter Sorgfalt
          recherchiert. Dennoch kann der Anbieter keine Haftung für die
          Richtigkeit, Vollständigkeit und Aktualität der bereit gestellten
          Informationen übernehmen. Die Informationen sind insbesondere auch
          allgemeiner Art und stellen keine Rechtsberatung im Einzelfall dar.
          Zur Lösung von konkreten Rechtsfällen konsultieren Sie bitte unbedingt
          vorher einen Rechtsanwalt. Die Benutzung erfolgt ausschließlich auf
          eigenes Risiko."
        />
      </div>
      </React.Fragment>
    );
  }
}

export default AuthContainer;
