import React from "react";
import Contract from "./Contract";
import "./styles/Contracts.css";
import "./styles/GridLayout.css";

class ContractContainer extends React.Component {
  constructor(props){
    super(props)
    this.state={
      contracts:[]
    }
  }
  
  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }  
  componentDidMount() {
    this.fetchContracts();
  }
  
  sortByKey(array, key) {
    let tempArr = array;
    return tempArr.sort(function(a, b) {
        var x = a[key]; var y = b[key];
        return ((x < y) ? -1 : ((x > y) ? 1 : 0));
    });
}

  render() {
    return (
      <div className="contractContainer">
        {this.state.contracts.map(contract => {
          return (
            <Contract
              key={contract.id}
              className="col-5"
              name={contract.name}
              endDate={contract.laufzeitBis}
              price={contract.kosten}
              description={contract.beschreibung}
            />
          )
        })}
      </div>
    )
  }
}

export default ContractContainer;
