import React from "react";
import axios from "axios";
import "./styles/Contracts.css";
import "./styles/BuildControls.css";
import "./styles/Navigationbar.css";

const ContractData = props => {
  return (
    <React.Fragment>
      <p>
        <b>Name:</b>
        <br /> {props.name}
      </p>
      <p>
        <b>ID:</b>
        <br />
        {props.id}
      </p>
      <p>
        <b>Preis: </b>
        <br />
        {props.price}â‚¬
      </p>
      <p>
        <b>Beschreibung: </b>
        <br />
        {props.description}
      </p>
    </React.Fragment>
  );
};

const handleDeleteBill = (id) =>{
  let obj = {
    name: this.state.name,
    userId: 1,
    kosten: this.state.price,
    beschreibung: this.state.description
  };

  let url = "http://localhost:9000/bills/insertBill";
  axios
    .post(url, obj)
    .then(res => console.log(res))
    .catch(e => console.log(e));

  alert("Eine Neue Rechnung wurde angelegt.");
  this.setState();
}

const bill = props => (
  <div className="contractBox">
    <div>
      <ContractData
        name={props.name}
        id={props.id}
        price={props.price}
        description={props.description}
      ></ContractData>
      <div className="contractControl">
        <div className="toolbarNavigationItems">
          <ul>
            <li>
              <button onClick={this.handleDeleteButton} className="button1">Delete</button>
            </li>
            <li>
              <button className="button1">Edit</button>
            </li>
          </ul>
        </div>
      </div>
    </div>
  </div>
);

export default bill;
