  render() {
    return (
      <div className="content-container">
        <Header
          title="Rechnungen"
          text="Herzlich Willkommen, hier befinden sich all Ihre Rechnungen."
        />
        <div className="topBar">
          <FilterSelect />

          <Link to="/newBill">
            <button className="button4">Neue Rechnung</button>
          </Link>
        </div>
        <BillContainer bills={this.state.bills}/>
      </div>
    );
  }
}

export default BillPage;
