import React from "react";
import "./styles/ContentContainer.css";
import "./styles/Forms.css";
import "./styles/GridLayout.css";
import HomePage from "./Pages/HomePage";
import ContractPage from "./Pages/ContractPage";
import BillPage from "./Pages/BillPage";
import CategoryPage from "./Pages/CategoryPage";
import ProfilePage from "./Pages/ProfilePage";




class ContentContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loggedIn: true,
      userId: "",
      title: this.props.title,
      currentPage: this.props.currentPage,
      content: "",
      user: [],
      contracts: [
        {
          name: "Autoversicherung",
          startDate: "02.09.2014",
          endDate: "1.10.2024",
          price: "100",
          description: "Pro Monat"
        },
        {
          name: "Handyvertrag",
          startDate: "01.01.2018",
          endDate: "01.01.2020",
          price: 34.99,
          description: "Telekom Handyvertrag pro Monat"
        },
        {
          name: "Krankenversicherung",
          startDate: "01.08.2019",
          endDate: "ohne Laufzeit",
          price: 200,
          description: "TK"
        },
        {
          name: "Krankenversicherung",
          startDate: "01.08.2019",
          endDate: "ohne Laufzeit",
          price: 200,
          description: "TK"
        },
        {
          name: "Krankenversicherung",
          startDate: "01.08.2019",
          endDate: "ohne Laufzeit",
          price: 200,
          description: "TK"
        }
      ],
      bills: [
        {
          name: "Edeka Rechnung",
          date: "01.02.2019",
          price: 57,
          description: ""
        },
        {
          name: "Edeka Rechnung",
          date: "01.02.2019",
          price: 57,
          description: ""
        }
      ]
    };
    this.pageChanger = this.pageChanger.bind(this);
  }

  pageChanger ()  {
    let page = null;
    switch(this.state.currentPage){
      case "0":  page = <HomePage title={this.state.title}/>;
        break;
      case "1": page =  <BillPage title={this.state.title} bills={this.state.bills}/>;
        break;
      case "2": page = <ContractPage title={this.state.title} contracts={this.state.contracts}/>;
        break;
      case "3": page = <ContractPage title={this.state.title} contracts={this.state.contracts}/>;
        break;
      case "4": page = <ContractPage title={this.state.title} contracts={this.state.contracts}/>;
        break;
      case "5": page = <ContractPage title={this.state.title} contracts={this.state.contracts}/>;
        break;
      case "6": page = <ContractPage title={this.state.title} contracts={this.state.contracts}/>;
        break;
      default: return;
    }
    this.setState({content: {page}});
  }

  render() {
    return (
      <div>
        <HomePage title={this.state.title}/>
        <BillPage title={this.state.title} bills={this.state.bills}/>
        <ContractPage title={this.state.title} contracts={this.state.contracts}/>
        <CategoryPage title={this.state.title} />
        <ProfilePage title={this.state.title} />
        {this.pageChanger}
        {this.state.content}
      </div>
    );
  }
}

export default ContentContainer;
