import React from "react";
import "../styles/Auth.css";
class RegisterForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: ""
    };
  }

  render() {
    return (
      <div className="authFormDiv">
        <h2>Registrieren Sie sich:</h2>
        <form className="authForm">
          <label>Username: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>Name: </label>
          <input
            className="authInput"
            type="password"
            onChange={this.handlePasswordChange}
            required
          ></input>
          <label>Vorname: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>E-Mail: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>Passwort </label>
          <input
            className="authInput"
            type="password"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>E-Mail: </label>
          <input
            className="authInput"
            type="test"
            value={new Date()}
            required
          ></input>
          
        </form>
        <div className="authButtonDiv">
          <button className="buttonA" onClick={this.loginUser}>
            Login
          </button>
          <button className="buttonA" onClick={this.handleRegisterUserClick}>
            Registrieren
          </button>
        </div>
      </div>
    );
  }
}

export default RegisterForm;
