import React from "react";
import Bill from "../Bill";
import axios from "axios";
import "../styles/Contracts.css";

class BillsByCategory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      id: ""
    };
  }

  componentDidMount(){
    this.setState({bills: this.props.bills})
  }

  render() {
    console.log(this.state.id);
    return (
      <div className="contractContainer">
        {this.state.bills.map(bill => {
          if (
            bill.catId1 === 1 ||
            bill.catId2 === 1 ||
            bill.catId2 === 1
          ) {
            return (
              <Bill
                key={bill.id}
                name={bill.name}
                id={bill.id}
                price={bill.price}
                description={bill.beschreibung}
                catId1={bill.catId1}
                catId2={bill.catId2}
                catId3={bill.catId3}
                categories={this.props.categories}
              />
            );
          } else {
            return null;
          }
        })}
      </div>
    );
  }
}

export default BillsByCategory;
