import React from "react";

//Schleife bauen die einmal durch contracts und bills iteriert und jeweils die kosten addiert und als neue Variable zurück gibt
const sumContractCosts = contracts => {
  let contractSum = 0;
  contracts.map(contract => {
    contractSum = contractSum + contracts.kosten;
    return <div>{contractSum}</div>;
  });
};


const sumContracts = props => {
  let contracts = props.contracts;


  return (
    <div className="sumObj">
      <div>
        <form>
          <h3>{props.title}</h3>
          <ul className="HomeList">
            <li>{sumContractCosts(contracts)}€</li>
          </ul>
        </form>
      </div>
    </div>
  );
};

export default sumContracts;
