import React from "react";
import "./styles/GridLayout.css";
import "./styles/FilterSelect.css";
import FilterMinMax from "./FilterMinMax";

class FilterSelect extends React.Component {
  state = {
    options: [
      { value: "0", name: "(nichts ausgewählt)" },
      { value: "1", name: "Datum (aufsteigend)" },
      { value: "2", name: "Datum (absteigend)" },
      { value: "3", name: "Kosten (aufsteigend)" },
      { value: "4", name: "Kosten (absteigend)" },
      { value: "5", name: "Name (aufsteigend)" },
      { value: "6", name: "Name (absteigend)" }
    ],
    value: "?",
    renderedMinMax: null
  };

  filterSelectHandler = event => {
    let optionValue;

    let mySelect = document.getElementById("selector");
    optionValue = event.target.value;
    switch (optionValue) {
      case "1":
        this.setState(this.state.renderedMinMax, <FilterMinMax type="date" />);
        break;
      case "2":
        this.setState(this.state.renderedMinMax, <FilterMinMax type="date" />);
        break;
      case "3":
        this.setState(
          this.state.renderedMinMax,
          <FilterMinMax type="number" />
        );
        break;
      case "4":
        this.setState(
          this.state.renderedMinMax,
          <FilterMinMax type="number" />
        );
        break;
      case "5":
        this.setState(this.state.renderedMinMax, <FilterMinMax type="text" />);
        break;
      case "6":
        this.setState(this.state.renderedMinMax, <FilterMinMax type="text" />);
        break;
      default:
        this.setState(this.state.renderedMinMax, null);
    }
  };

  render() {
    const { options, value } = this.state;
    return (
      <div className="topBar col-11">
        <div>
          <label> Sortieren nach: </label>
          <select
            onChange={this.filterSelectHandler}
            name="Sortieren nach"
            value={value}
          >
            {options.map(item => (
              <option key={item.value} value={item.value}>
                {item.name}
              </option>
            ))}
          </select>
        </div>
        <div>{this.state.renderedMinMax}</div>
      </div>
    );
  }
}

export default FilterSelect;
