import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./styles/Navigationbar.css";

class Searchbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchContent: "",
      bills: []
    };
  }

  fetchSearchResults = e => {
    e.preventDefault();
    let obj = {
      message: this.state.searchContent
    };

    let url = "http://localhost:9000/bills/search";
    axios
      .post(url, obj)
      .then(res => this.setState({ bills: res.data }))
      .catch(e => console.log(e));

    console.log(this.state.bills);
  };

  render() {
    return (
      <form className="searchbar" onSubmit={this.fetchSearchResults}>
        <input
          className="input1"
          id="search"
          label="Suche"
          type="text"
          placeholder="Was suchen Sie?"
        ></input>
        <Link to="/search-result">
          <input className="button4" type="submit" value="go" />
        </Link>
      </form>
    );
  }
}

export default Searchbar;
