import React from "react";
import Bill from "./Bill";
import Contract from "./Contract";
import "./styles/Home.css";
import "./styles/AddForm.css";

const BillsByCategory = (catId, bills, categories) => {
  bills.map(bill => {
    if (
      bill.catId1 === catId ||
      bill.catId2 === catId ||
      bill.catId3 === catId
    ) {
        return(
        <Bill 
        name={bill.name}
        id={bill.id}
        price={bill.price}
        description={bill.beschreibung}
        catId1={bill.catId1}
        catId2={bill.catId2}
        catId3={bill.catId3}
        categories={this.props.categories}/>);
    }
  });
};

class FilterCategories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      contracts: [],
      selectId: 1
    };
  }

  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }
  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
  }

  filterSelectHandler = e => {
    this.setState({ selectId: e.target.value });
  };

  handleFilterButton = e => {};

  render() {
    return (
      <div className="HomeContainer">
        <div>
          <label>Nach welcher Kategorie filtern? </label>
          <select
            className="inputAdd"
            onChange={this.filterSelectHandler}
            name="Sortieren nach"
          >
            {this.props.categories.map(category => {
              if (category.id === 1) {
                return (
                  <option key={category.id} value={category.id}>
                    ohne Kategorie
                  </option>
                );
              } else {
                return (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                );
              }
            })}
          </select>
          <button className="button4" onClick={this.handleFilterButton}>
            Go
          </button>
        </div>
      </div>
    );
  }
}

export default FilterCategories;
