import React from "react";
import "./styles/AddForm.css";
import "./styles/BuildControls.css";
import { Link } from "react-router-dom";
import axios from "axios";

class NewBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      price: "",
      description: ""
    };
  }

  handlePriceChange = e => {
    this.setState({ price: e.target.value });
  };

  handleDescriptionChange = e => {
    this.setState({ description: e.target.value });
  };

  handleNameChange = e => {
    this.setState({ name: e.target.value });
  };

  handleAddSubmit = e => {
    e.preventDefault();
    let obj = {
      name: this.state.name,
      userId: 1,
      kosten: this.state.price,
      beschreibung: this.state.description
    };

    let url = "http://localhost:9000/bills/insertBill";
    axios
      .post(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Eine Neue Rechnung wurde angelegt.");
    window.location.reload();
  };

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleAddSubmit}>
          <h3>Erstellen Sie eine neue Rechnung</h3>
          <label>Name:</label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            placeholder="..."
            value={this.state.name}
            onChange={this.handleNameChange}
            required
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            placeholder="..."
            value={this.state.price}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            placeholder="..."
            value={this.state.description}
            onChange={this.handleDescriptionChange}
            required
          ></input>
          <select
            lassName="inputAdd"
            type="text"
            placeholder="..."
            name="category"
            onChange={this.handleCategoryChange}
          >
            <option value="">/</option>
            <option value="1">Versicherung</option>
            <option value="2">Entertainment</option>
            <option value="3">Lebensmittel</option>
            <option value="4">Verkehr</option>
            <option value="5">Bildung</option>
          </select>
          <input className="button4" type="submit" value="Erstellen" />
          <Link to="/bills">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default NewBill;
