import React from "react";
import axios from "axios";
import EditBill from "./EditBill";
import { Link } from "react-router-dom";
import "./styles/Contracts.css";
import "./styles/BuildControls.css";
import "./styles/Navigationbar.css";

const ContractData = props => {
  let categories = props.categories;
  return (
    <React.Fragment>
      <p>
        <b>Rechnungs-ID:</b>
        <br />
        {props.id}
      </p>
      <p>
        {" "}
        <b>Name:</b>
        <br /> {props.name}
      </p>
      <p>
        <b>Preis: </b>
        <br />
        {props.price}€
      </p>
      <p>
        <b>Beschreibung: </b>
        <br />
        {props.description}
      </p>
      <p>
        <b>Kategorien: </b>
        <br />
        {categories.map(category => {
          if (
            category.id === props.catId1 ||
            category.id === props.catId2 ||
            category.id === props.catId3
          ) {
            if (category.id !== 1)
              return (
                <React.Fragment key={category.name}>
                  {category.name}
                  <br />
                </React.Fragment>
              );
          }
          return null;
        })}
      </p>
    </React.Fragment>
  );
};

class Bill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      content: null
    };
  }
  categories = this.props.categories;
  handleDeleteBill = e => {
    e.preventDefault();
    let id = this.props.id;
    let url = `http://localhost:9000/bills/deleteBill/${id}`;
    axios({
      method: "DELETE",
      url: url,
      data: {
        id: this.props.id
      }
    });
    alert(`Die Rechnung mit der ID ${id} wurde gelöscht.`);
    window.location.reload();
  };
renderEditBill(){
  this.setState({content: <EditBill id={this.props.id}/>});
  window.location.reload();
}
  render() {
    return (
      <div className="contractBox">
        <div>
          <ContractData
            name={this.props.name}
            id={this.props.id}
            price={this.props.price}
            description={this.props.description}
            catId1={this.props.catId1}
            catId2={this.props.catId2}
            catId3={this.props.catId3}
            categories={this.props.categories}
          ></ContractData>
          <div className="contractControl">
            <div className="toolbarNavigationItems">
              <ul>
                <li>
                  <button className="button1" onClick={this.handleDeleteBill}>
                    Delete
                  </button>
                </li>
                <li>
                  <button className="button1" onClick={this.renderEditBill}>
                    Edit
                  </button>
                  {this.state.content}
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Bill;
