import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class EditBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bill: [],
      billId : this.props.location.state
    };
  }

  
  
  handleEditSubmit() {}

  fetchBills() {
    fetch(`/bills/24`)
      .then(res => res.json())
      .then(bill => this.setState({ bill: bill }))
      .then(err => err);
  }

  componentWillMount() {
    this.fetchBills();
  }

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleEditSubmit}>
          <h3>
            Hier sind Ihre Rechnungsdaten.<br></br> Sie können sie nun
            bearbeiten.
          </h3>
          <label>Name: </label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            value={this.state.bill.name}
            onChange={this.handleNameChange}
            
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            value={this.state.bill[0]}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            value={this.state.bill.beschreibung}
            onChange={this.handleDescriptionChange}
            required
          ></input>
          <input
            className="button4"
            type="submit"
            value="Bearbeitung bestätigen"
          />
          <Link to="/bills">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default EditBill;
