import React from "react";
import "./styles/AddForm.css";
import "./styles/BuildControls.css";
import { Link } from "react-router-dom";
import axios from "axios";

class NewBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      price: "",
      description: "",
      handleNameChange: function(e) {
        this.setState({ name: e.target.value });
      }.bind(this),
      handlePriceChange: function(e) {
        this.setState({ price: e.target.value });
      }.bind(this),
      handleDescriptionChange: function(e) {
        this.setState({ description: e.target.value });
      }.bind(this),
      handleAddSubmit: function(e) {
        e.preventDefault();
        let obj = {
          name: this.state.name,
          userid: 1,
          kosten: this.state.price,
          beschreibung: this.state.description
        };
        let url = "http://localhost:9000/insertBill";
        axios
          .post(url, obj)
          .then(res => console.log(res))
          .catch(e => console.log(e));
      }.bind(this)
    };
  }

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleAddSubmit}>
          <h3>Erstellen Sie eine neue Rechnung</h3>
          <label>Name:</label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            placeholder="..."
            
            onChange={this.handleNameChange}
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            placeholder="..."
            onChange={this.handlePriceChange}
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            placeholder="..."
            onChange={this.handleDescriptionChange}
          ></input>
          <input type="submit" value="Erstellen" className="button4"></input>
          <Link to="/bills">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default NewBill;
