import React from "react";
import BillsByCategory from "./BillsByCategory";
import axios from "axios";
import Bill from "../Bill";
import "../styles/Home.css";
import "../styles/AddForm.css";
import "../styles/Contracts.css";

class FilterCategories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      content: <div></div>,
      selectId: 1,
      bills: []
    };
  }

  fetchBillsByCategory() {
    let obj = {
      id: this.state.selectId,
      userId: 1
    };
    let url = "http://localhost:9000/bills/category";
    axios
      .post(url, obj)
      .then(res => this.setState({bills: res.data}))
      .catch(e => console.log(e));
  }

  handleButtonClick = () => {
    this.fetchBillsByCategory();
  }

  componentDidMount() {
    this.fetchBillsByCategory();
  }

  filterSelectHandler = e => {
    this.setState({ selectId: e.target.value });
  };

  render() {
    return (
      <div className="content-container">
        <div className="content-container">
          <label className="labelCat">Nach welcher Kategorie filtern? </label>
          <form className="BuildControls">
            <select
              className="inputAdd"
              onChange={this.filterSelectHandler}
              value={this.state.selectId}
              name="Sortieren nach"
            >
              {this.props.categories.map(category => {
                if (category.id === 1) {
                  return (
                    <option key={category.id} value={category.id}>
                      ohne Kategorie
                    </option>
                  );
                } else {
                  return (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  );
                }
              })}
            </select>
          </form>
          <button onClick={this.handleButtonClick} className="button4">Go</button>
        </div>
        {this.state.bills.map(bill => {
          if (bill.catId1 === 1 || bill.catId2 === 1 || bill.catId2 === 1) {
            return (
              <Bill
                key={bill.id}
                name={bill.name}
                id={bill.id}
                price={bill.kosten}
                description={bill.beschreibung}
                catId1={bill.catId1}
                catId2={bill.catId2}
                catId3={bill.catId3}
                categories={this.props.categories}
              />
            );
          } else {
            return null;
          }
        })}
      </div>
    );
  }
}

export default FilterCategories;
