    }

    fetchBills() {
        fetch("http://localhost/9000/bills/"+this.state.id)
          .then(res => res.json())
          .then(bill => this.setState({ bill: bill }))
          .then(err => err);
      }

    componentDidMount() {
       this.fetchBills();
    }
    

    render () {
        return(
            <div className="newContainer">
        <form className="newForm" onSubmit={this.handleEditSubmit}>
          <h3>Erstellen Sie eine neue Rechnung</h3>
          <label>Name:</label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            placeholder="..."
            value={this.state.bill[0].name}
            onChange={this.handleNameChange}
            required
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            placeholder="..."
            value={this.state.bill[0].kosten}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            placeholder="..."
            value={this.state.bill[0].beschreibung}
            onChange={this.handleDescriptionChange}
            required
          ></input>
