import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./styles/Contracts.css";
import "./styles/BuildControls.css";
import "./styles/Navigationbar.css";

const ContractData = props => {
  let categories = props.categories;
  return (
    <React.Fragment>
      <p>
        <b>Rechnungs-ID:</b>
        <br />
        {props.id}
      </p>
      <p>
        <b>Name:</b>
        <br /> {props.name}
      </p>
      <p>
        <b>Preis: </b>
        <br />
        {props.price}â‚¬
      </p>
      <p>
        <b>Beschreibung: </b>
        <br />
        {props.description}
      </p>
      <p>
        <b>Kategorie: </b>
        <br />
        {categories.map(category => {
          if (category.id === props.catId) {
            return categories.name;
          }
          return null;
        })}
      </p>
    </React.Fragment>
  );
};

class Bill extends React.Component {
  categories = this.props.categories;
  handleDeleteBill = e => {
    e.preventDefault();
    let id = this.props.id;
    let url = `http://localhost:9000/bills/deleteBill/${id}`;
    axios({
      method: "DELETE",
      url: url,
      data: {
        id: this.props.id
      }
    });
    alert(`Die Rechnung mit der ID ${id} wurde gelÃ¶scht.`);
    window.location.reload();
  };

  render() {
    return (
      <div className="contractBox">
        <div>
          <ContractData
            name={this.props.name}
            id={this.props.id}
            price={this.props.price}
            description={this.props.description}
            catId={this.props.catId}
            categories={this.categories}
          ></ContractData>
          <div className="contractControl">
            <div className="toolbarNavigationItems">
              <ul>
                <li>
                  <button className="button1" onClick={this.handleDeleteBill}>
                    Delete
                  </button>
                </li>
                <li>
                  <Link
                    to={{
                      pathname: "/EditBill",
                      state: { billId: this.props.id }
                    }}
                  >
                    <button className="button1">Edit</button>
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Bill;
