import React from "react";
import Header from "../Header";
import HomeContainer from "../HomeContainer";
import FilterSelect from "../FilterSelect";
import "../styles/Home.css";

class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      contracts: [],
      bills: []
    };
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }

  fetchCategories() {
    fetch("/categories")
      .then(res => res.json())
      .then(categories => this.setState({ categories: categories }))
      .then(err => err);
  }

  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
  }

  render() {
    return (
      <div className="content-container">
        <Header
          title="Home"
          text="Herzlich Willkommen, Sie befinden sich auf der Startseite wo Sie eine Übersicht über Ihre aktuellen Dokumente haben."
        />
        <FilterSelect />
        <HomeContainer
          contracts={this.state.contracts}
          bills={this.state.bills}
        />
      </div>
    );
  }
}

export default HomePage;
