import React from "react";
import "./styles/GridLayout.css";
import "./styles/FilterSelect.css";
import FilterMinMax from "./FilterMinMax";

class FilterSelect extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: this.props.bills,
      categories: this.props.categories,
      options: [
        { value: "0", name: "(nichts ausgewählt)" },
        { value: "1", name: "Kosten (aufsteigend)" },
        { value: "2", name: "Kosten (absteigend)" },
        { value: "3", name: "Datum (aufsteigend)" },
        { value: "4", name: "Datum (absteigend)" },
        { value: "5", name: "Name (aufsteigend)" },
        { value: "6", name: "Name (absteigend)" }
      ],
      value: "?",
      renderedMinMax: null
    };
  }

  filterSelectHandler = event => {
    let optionValue;
    optionValue = event.target.value;
    switch (optionValue) {
      case "1":
        this.setState({ renderedMinMax: <FilterMinMax type="number" /> });
        break;
      case "2":
        this.setState({ renderedMinMax: <FilterMinMax type="number" /> });
        break;
      case "3":
        this.setState({ renderedMinMax: <FilterMinMax type="date" /> });
        break;
      case "4":
        this.setState({ renderedMinMax: <FilterMinMax type="date" /> });
        break;
      default:
        this.setState({ renderedMinMax: null });
    }
  };
  componentDidMount() {
    this.setState({ bills: this.props.bills });
    this.setState({ categories: this.props.categories });
  }

  render() {
    return (
      <div className="topBar">
        <div>
          <label> Sortieren nach: </label>
          <select onChange={this.filterSelectHandler} name="Sortieren nach">
            {this.state.categories.map(item => (
              <option key={item.id} value={item.id}>
                {item.name}
              </option>
            ))}
          </select>
        </div>
        <div>{this.state.renderedMinMax}</div>
      </div>
    );
  }
}

export default FilterSelect;
