import React from "react";
import "./styles/Profile.css";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: {},
      user: {
        id: 1,
        username: "username",
        firstName: "firstname",
        name: "name",
        email: "email@email.de",
        password: "...",
        signUpDate: "2019-12-12"
      }
    };
  }

  fetchUsers() {
    fetch("/users")
      .then(res => res.json())
      .then(users => this.setState({ users: users }))
      .catch(err => err);
  }
  componentDidMount() {
    this.fetchUsers();
  }

  // noch nicht fertig
  handleInput = props => {
    this.props.editUserData(props.key, props.event.target.value);
  };

  handleEdit = (type, placeholderText, key) => {
    return (
      <div>
        <label>
          <input
            onChange={this.handleInput(key)}
            type={type}
            placeholder={placeholderText}
          />
        </label>
      </div>
    );
  };
  //

  render() {
    return (
      <div className="profile">
        <section className="profileSection">
          <ul>
            <li>
              <div className="pDiv">
                <div>
                  <table>
                    <tr>
                      <th>User ID:</th>
                      <th>Username:</th>
                      <th>Name:</th>
                      <th>Vorname:</th>
                      <th>E-Mail:</th>
                      <th>angemeldet seit:</th>
                    </tr>
                    <tr>
                      <td>{this.state.user.id}</td>
                      <td>{this.state.user.username}</td>
                      <td>{this.state.user.name}</td>
                      <td>{this.state.user.firstName}</td>
                      <td>{this.state.user.email}</td>
                      <td>{this.state.user.signUpDate}</td>
                    </tr>
                    
                  </table>
                  </div>
                  </div>
            </li>
          </ul>
        </section>
        <button className="button3">Edit</button>
      </div>
    
    );
  }
}

export default Profile;
