import React from "react";
import axios from "axios";
import "./styles/Navigationbar.css";

class Searchbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchContent: ""
    };
  }

  fetchSearchResults = e => {
    e.preventDefault();
    let obj = {
      message: this.state.searchContent
    };

    let url = "http://localhost:9000/bills/search";
    axios
      .post(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Eine Neue Rechnung wurde angelegt.");
    window.location.reload();
  };

  render() {
    return (
      <form className="searchbar" onSubmit={this.fetchSearchResults}>
        <input
          className="input1"
          id="search"
          label="Suche"
          type="text"
          placeholder="Was suchen Sie?"
        ></input>
        <input className="button4" type="submit" value="go" />
      </form>
    );
  }
}

export default Searchbar;
