import React from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import "./styles/Contracts.css";
import "./styles/BuildControls.css";
import "./styles/Navigationbar.css";

const ContractData = props => {
  return (
    <React.Fragment>
      <p>
        <b>Name:</b>
        <br /> {props.name}
      </p>
      <p>
        <b>ID:</b>
        <br />
        {props.id}
      </p>
      <p>
        <b>Preis: </b>
        <br />
        {props.price}€
      </p>
      <p>
        <b>Beschreibung: </b>
        <br />
        {props.description}
      </p>
    </React.Fragment>
  );
};

class Bill extends React.Component {
  handleDeleteBill = e => {
    e.preventDefault();
    let id = this.props.id;
    let url = `http://localhost:9000/bills/deleteBill/${id}`;
    axios({
      method: "DELETE",
      url: url,
      data: {
        id: this.props.id
      }
    });
    alert(`Die Rechnung mit der ID ${id} wurde gelöscht.`);
    window.location.reload();
  };

  render() {
    return (
      <div className="contractBox">
        <div>
          <ContractData
            name={this.props.name}
            id={this.props.id}
            price={this.props.price}
            description={this.props.description}
            catId={this.props.catId}
          ></ContractData>
          <div className="contractControl">
            <div className="toolbarNavigationItems">
              <ul>
                <li>
                  <button className="button1" onClick={this.handleDeleteBill}>
                    Delete
                  </button>
                </li>
                <li>
                  <Link to={{pathname:"/EditBill", state: {billId: this.props.id}}} ><button className="button1">Edit</button></Link>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Bill;
