import React from "react";
import "./styles/AddForm.css";
import "./styles/BuildControls.css";
import { Link } from "react-router-dom";
import axios from "axios";

class NewContract extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      endDate: "",
      price: "",
      description: ""
    };
  }

  handleNameChange = e => {
    this.setState({ name: e.target.value });
  };

  handleEndDateChange = e => {
    this.setState({ endDate: e.target.value });
  };
  handlePriceChange = e => {
    this.setState({ price: e.target.value });
  };

  handleDescriptionChange = e => {
    this.setState({ description: e.target.value });
  };

  handleAddSubmit = e => {
    e.preventDefault();
    const obj = {
      userId: 1,
      name: this.state.name,
      kosten: this.state.price,
      laufzeitBis: this.state.endDate,
      beschreibung: this.state.description,
      catId: this.state.catId
    };
    let url = "http://localhost:9000/contracts/insertContract";
    axios
      .post(url, obj)
      .then(res => console.log(res.data))
      .then(this.setState({ name: "", price: "", description: "" , catId: ""}));
    alert("Sie haben einen neuen Vertrag angelegt.");
    window.location.reload();
  };

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleAddSubmit}>
          <h3>Erstellen Sie einen neuen Vertrag</h3>
          <label>Name:</label>
          <input
            className="inputAdd"
            type="text"
            placeholder="..."
            name="name"
            value={this.state.name}
            onChange={this.handleNameChange}
            required
          ></input>
          <label>Ende:</label>
          <input
            className="inputAdd"
            type="date"
            placeholder="..."
            name="endDate"
            value={this.state.endDate}
            onChange={this.handleEndDateChange}
            required
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            placeholder="..."
            name="price"
            value={this.state.price}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            placeholder="..."
            name="description"
            value={this.state.description}
            onChange={this.handleDescriptionChange}
            required
          ></input>
          <label>Kategorie:</label>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="description"
            onChange={this.handleCategoryChange}
          >
            <option value="">/</option>
            <option value="1">Versicherung</option>
            <option value="2">Entertainment</option>
            <option value="3">Lebensmittel</option>
            <option value="4">Verkehr</option>
            <option value="5">Bildung</option>
          </select>
          <input className="button4" type="submit" value="Erstellen" />
          <Link to="/contracts">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default NewContract;
