import React from "react";
import "./styles/GridLayout.css";
import "./styles/FilterSelect.css";
import FilterMinMax from "./FilterMinMax";

class FilterSelect extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: "",
      options: [
        { value: "0", name: "(nichts ausgewählt)" },
        { value: "1", name: "Kosten (aufsteigend)" },
        { value: "2", name: "Kosten (absteigend)" },
        { value: "3", name: "Datum (aufsteigend)" },
        { value: "4", name: "Datum (absteigend)" },
        { value: "5", name: "Name (aufsteigend)" },
        { value: "6", name: "Name (absteigend)" }
      ],
      value: "?",
      renderedMinMax: <FilterMinMax type="number" />,
      

    };
  }

  render() {
    return (
      <div className="topBar">
        <div>
          <label> Sortieren nach: </label>
          <select onChange={this.filterSelectHandler} name="Sortieren nach">
            {this.props.categories.map(item => (
              <option key={item.id} value={item.id}>
                {item.name}
              </option>
            ))}
          </select>
        </div>
        <div>{this.state.renderedMinMax}</div>
        <div className="sortedBillsDiv">

        </div>
      </div>
    );
  }
}

export default FilterSelect;
