import React from "react";
import "./styles/AddForm.css";
import "./styles/BuildControls.css";
import { Link } from "react-router-dom";
import axios from "axios";

class NewCategory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      categoryId:""
    };
  }

  handleNameChange = e => {
    this.setState({ name: e.target.value });
  };

  handleCategoryDeleteChange = e => {
      this.setState({categoryId: e.target.value})
  }
  
  handleAddSubmit = e => {
    e.preventDefault();
    const obj = {
      name: this.state.name
    };
    let url = "http://localhost:9000/categories/insertCategory";
    axios
      .post(url, obj)
      .then(res => console.log(res.data))
      .then(
        this.setState({
          name: ""
        })
      );
    alert("Sie haben eine neue Kategorie angelegt.");
    window.location.reload();
  };

  handleDeleteContract = e => {
    e.preventDefault();
    let id = this.props.id;
    let url = `http://localhost:9000/contracts/deleteContract/${id}`;
    axios({
      method: "DELETE",
      url: url,
      data: {
        id: this.props.id
      }
    });
    alert(`Der Vertrag mit der ID ${id} wurde gelöscht.`);
    window.location.reload();
  };

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleAddSubmit}>
          <h3>Erstellen Sie eine neue Kategorie</h3>
          <label>Tragen Sie den Namen ein:</label>
          <input
            className="inputAdd"
            type="text"
            placeholder=""
            name="name"
            value={this.state.name}
            onChange={this.handleNameChange}
            required
          ></input>
          <input className="button4" type="submit" value="Erstellen" />
          <Link to="/home">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
        <p></p>
        <form className="newForm" onSubmit={this.handleAddSubmit}>
          <h3>Löschen Sie eine Kategorie.</h3>
          <label>Wählen Sie die Kategorie die gelöscht werden soll: </label>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="category3"
            onChange={this.handleCategoryDeleteChange}
          >
            {this.props.categories.map(category => {
              return (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              );
            })}
          </select>
          <input className="button4" type="submit" value="Löschen" />

          <Link to="/home">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}
export default NewCategory;
