import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const putMethod = {
  method: "PUT",
  headers: {
    "Content-type": "application/json; charset=UTF-8"
  }
};

class EditBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: {
        id: "",
        userId: "",
        name: "",
        kosten: "",
        beschreibung: "",
        catId1: "",
        catId2: "",
        catId3: ""
      },
      id: ''
    };
  }
  tempId = this.props.location.state;
  // PUT REQUEST KLAPPT NOCH NICHT
  handleEditSubmit = e => {
    e.preventDefault();

    let url = `http://localhost9000/updateBill`;

    fetch(url, this.putMethod)
      .then(response => response.json())
      .then(data => console.log(data))
      .catch(err => console.log(err));

    alert("Die Rechnung wurde aktualisiert.");
    window.location.reload();
  };

  handlePriceChange = e => {
    this.setState({ bills: { kosten: e.target.value } });
  };

  handleDescriptionChange = e => {
    this.setState({ bills: { beschreibung: e.target.value } });
  };

  handleNameChange = e => {
    this.setState({ bills: { name: e.target.value } });
  };

  handleCategoryChange = e => {
    this.setState({ bills: { catId: e.target.value } });
  };
  //Variable noch gehardcodet in der URL
  fetchBills() {
    fetch(`/bills/1`)
      .then(res => res.json())
      .then(bill => this.setState({ bills: bill }))
      .then(err => err);
  }

  componentDidMount() {
    this.setState({id: this.tempId})
    this.fetchBills();
  }

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleEditSubmit}>
          <h3>
            Hier sind Ihre Rechnungsdaten.<br></br> Sie können die Rechnung
            jetzt bearbeiten.
            <br></br>
            <br></br>
            Rechnungs-ID: {this.state.bills.id}
          </h3>
          <label>Name: </label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            value={this.state.bills.name}
            onChange={this.handleNameChange}
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            value={this.state.bills.kosten}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            value={this.state.bills.beschreibung}
            onChange={this.handleDescriptionChange}
            required
          ></input>
          <label>Kategorie: </label>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="category"
            value={this.state.bills.catId}
            onChange={this.handleCategoryChange}
          >
            {this.props.categories.map(category => {
              return (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              );
            })}
          </select>
          <input
            className="button4"
            type="submit"
            value="Bearbeitung bestätigen"
          />
          <Link to="/bills">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default EditBill;
