import React from 'react';
import Bill from "../Bill";
import "../styles/Contracts.css";

const billsByCategory = props => {
    return (
      <div className="contractContainer">
        {props.bills.map(bill => {
          if (
            bill.catId1 === props.catId ||
            bill.catId2 === props.catId ||
            bill.catId3 === props.catId
          ) {
            return (
              <Bill
                name={bill.name}
                id={bill.id}
                price={bill.price}
                description={bill.beschreibung}
                catId1={bill.catId1}
                catId2={bill.catId2}
                catId3={bill.catId3}
                categories={props.categories}
              />
            );
          }
        })}
      </div>
    );
  };

  export default billsByCategory;
