import React from "react";
import BillsByCategory from "./BillsByCategory";
import Contract from "../Contract";
import "../styles/Home.css";
import "../styles/AddForm.css";
import "../styles/Contracts.css";

// EIGENE KOMPONENTEN DARAUS BAUEN DAMIT DER STATE NEU GESTZT WIRD UM RENDER ZU TRIGGERN!



const ContractsByCategory = props => {
  return (
    <div className="contractContainer">
      {props.contracts.map(contract => {
        if (
          contract.catId1 === props.catId ||
          contract.catId2 === props.catId ||
          contract.catId3 === props.catId
        ) {
          return (
            <React.Fragment>
              <Contract
                className=""
                id={contract.id}
                name={contract.name}
                endDate={contract.laufzeitBis}
                price={contract.price}
                description={contract.beschreibung}
                catId1={contract.catId1}
                catId2={contract.catId2}
                catId3={contract.catId3}
                categories={props.categories}
              />
            </React.Fragment>
          );
        }
      })}
    </div>
  );
};

class FilterCategories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      contracts: [],
      selectId: ""
    };
  }

  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }
  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
  }

  filterSelectHandler = e => {
    this.setState({ selectId: e.target.value });
  };

  handleFilterButton = e => {};

  render() {
    return (
      <div className="content-container">
        <div>
          <label>Nach welcher Kategorie filtern? </label>
          <select
            className="inputAdd"
            onChange={this.filterSelectHandler}
            name="Sortieren nach"
          >
            {this.props.categories.map(category => {
              if (category.id === 1) {
                return (
                  <option key={category.id} value={category.id}>
                    ohne Kategorie
                  </option>
                );
              } else {
                return (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                );
              }
            })}
          </select>
        </div>
        <div>
          <BillsByCategory
            catId={this.state.selectId}
            bills={this.state.bills}
            categories={this.props.categories}
          ></BillsByCategory>
        </div>
      </div>
    );
  }
}

export default FilterCategories;
