import React from "react";
import "./styles/GridLayout.css";
import "./styles/FilterSelect.css";
import FilterMinMax from "./FilterMinMax";

class FilterSelect extends React.Component {

  state = {
    options: [
      {name: "0", value: "(nichts ausgewählt)"},
      {name: "1", value: "Datum (aufsteigend)"},
      {name: "2", value: "Datum (absteigend)"},
      {name: "3", value: "Kosten (aufsteigend)"},
      {name: "4", value: "Kosten (absteigend)"},
      {name: "5", value: "Name (aufsteigend)"},
      {name: "6", value: "Name (absteigend)"},
    ]
  }

  filterSelectHandler = props => {
    let optionValue;
    let renderedMinMax;
    let mySelect = document.getElementById("selector");
    optionValue = mySelect.options[mySelect.selectedIndex].value;
    switch (optionValue) {
      case "1":
        renderedMinMax = <FilterMinMax type="date"/>;
        break;
      case "2":
        renderedMinMax = <FilterMinMax type="date"/>;
        break;
      case "3":
        renderedMinMax = <FilterMinMax type="number"/>;
        break;
      case "4":
        renderedMinMax = <FilterMinMax type="number"/>;
        break;
      case "5":
        renderedMinMax = <FilterMinMax type="text"/>;
        break;
      case "6":
        renderedMinMax = <FilterMinMax type="text"/>;
        break;
      default:
        renderedMinMax = null;
    }
    return renderedMinMax;
  };


  render() {
    return (
      <div className="topBar col-11">
        <label> Sortieren nach: </label>
        <select
          onChange={() => this.filterSelectHandler()}
          name="Sortieren nach"
          
        >
          <option value="0"> (nichts ausgewählt)</option>
          <option value="1"> </option>
          <option value="2"> </option>
          <option value="3"> </option>
          <option value="4"> </option>
          <option value="5"> </option>
          <option value="6"> </option>
        </select>
      </div>
    );
  }
}

export default FilterSelect;
