import React from "react";
import "../styles/Auth.css";
import "../styles/GridLayout.css";

class RegisterForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      name: '',
      firstName: '',
      email: '',
      password:''

    };
  }

  handleAddSubmit = e => {

  }

  render() {
    return (
      <div className="authFormDiv">
        <h2>Registrieren Sie sich:</h2>
        <form className="authForm">
          <label>Username: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>Name: </label>
          <input
            className="authInput"
            type="password"
            onChange={this.handlePasswordChange}
            required
          ></input>
          <label>Vorname: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>E-Mail: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>Passwort: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>Anmeldedatum: </label>
          <input
            className="authInput"
            type="test"
            value={new Date()
              .toISOString()
              .slice(0, 19)
              .replace("T", " ")}
            readonly
          ></input>
        </form>
        <div className="authButtonDiv">
          <button className="buttonA" onClick={this.registerUser}>
            Registrieren
          </button>
          <button className="buttonA" onClick={() => window.location.reload()}>
            Abbrechen
          </button>
        </div>
      </div>
    );
  }
}

export default RegisterForm;
