import React from "react";
import "./styles/Profile.css";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: []
    };
  }


// noch nicht fertig
  handleInput = props => {
    this.props.editUserData(props.key, props.event.target.value);
  };

  handleEdit = (type, placeholderText, key) => {
    return (
      <div>
        <label>
          <input
            onChange={this.handleInput(key)}
            type={type}
            placeholder={placeholderText}
          />
        </label>
      </div>
    );
  };
//

  render() {
    return (
      <div className="profile">
        <section className="profileSection">
          <ul>
            <li>
              {" "}
              <div className="pDiv">
                <div>Ihre User ID:</div>
                <div className="innerDiv">{this.state.user[0].id}</div>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Username:</div>
                <div className="innerDiv">{this.state.user[0].username}</div>
                <button className="button3">Edit</button>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Name:</div>
                <div className="innerDiv">{this.state.user[0].name}</div>
                <button className="button3">Edit</button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>Vorname:</div>
                <div className="innerDiv">{this.state.user[0].firstName}</div>
                <button className="button3">Edit</button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>E-Mail:</div>
                <div className="innerDiv">{this.state.user[0].email}</div>
                <button className="button3">Edit</button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>angemeldet seit:</div>
                <div className="innerDiv">{this.state.user[0].signUpDate}</div>
              </div>
            </li>
          </ul>
        </section>
      </div>
    );
  }
}

export default profile;
