import React from "react";
import "./styles/AddForm.css";
import "./styles/BuildControls.css";
class NewBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      price: "",
      description: "",
      handleNameChange: function(e) {
        this.setState({ name: e.target.value });
      },
      handlePriceChange: function(e) {
        this.setState({ price: e.target.value });
      },
      handleDescriptionChange: function(e) {
        this.setState({ description: e.target.value });
      },
      handleAddSubmit: function() {
        console.log("name: " + this.state.name);
        console.log("price: " + this.state.name);
        console.log("description: " + this.state.name);
      }
    };
  }

  render() {
    return (
      <div className="newContainer">
        
        <form onSubmit={this.handleAddSubmit} className="newForm">
        <h3>Erstellen Sie eine neue Rechnung</h3>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            placeholder="..."
            onChange={this.handleNameChange}
          ></input>
          <label>Preis:</label>
          <input
            type="number"
            name="price"
            placeholder="..."
            onChange={this.handlePriceChange}
          ></input>
          <label>Beschreibung:</label>
          <input
            type="text"
            name="description"
            placeholder="..."
            onChange={this.handleDescriptionChange}
          ></input>
          <button className="button4" onClick={this.handleAddSubmit}>Erstellen</button>
          <button className="button4">Abbrechen</button>
        </form>
      </div>
    );
  }
}

export default NewBill;
