import React from "react";
import ImpressumPage from "./components/Pages/ImpressumPage";
import NavigationBar from "./components/NavigationBar";
import ContentContainer from "./components/ContentContainer";
import Footer from "./components/Footer";
import "./App.css";

class ContractManager extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      apiResponse: [],
      loggedIn: true,
      user: [{id: 1,
        username: "testUser",
        firstname: "firstName",
        name: "name",
        email: "email@email.de",
        password: "passwort",
        signUpdate: "02.10.2019"}
      ],
      pageTitle: [
        "Home",
        "Rechnungen",
        "Verträge",
        "Kategorien",
        "Impressum",
        "Kontakt",
        "Profil"
      ],
      currentPage: 0

    };
  }

  callAPI() {
    fetch("http://localhost:9000/testAPI")
      .then(res => this.setState({ apiResponse: res }));
  }

  componentDidMount() {
    this.callAPI();
  }

  handlePageChange = pageId => {
    let oldPage = this.state.currentPage;

    if (pageId !== oldPage) {
      this.setState({ currentPage: pageId }); 
      alert(this.state.currentPage + oldPage);
    } else {
      return;
    }
  };

  render() {
    return (
      <div className="App">
        <header>
          <NavigationBar pageChanged={this.handlePageChange} />
        </header>
        <main>
          <ContentContainer
            loggedIn={this.state.loggedIn}
            userId={this.state.userId}
            title={this.state.pageTitle[this.state.currentPage]}
            currentPage={this.state.currentPage}
            user={this.state.user}
          />
          <div>
          {this.state.apiResponse.map(bill => {
          return (
            <p>{bill.id}</p>

          );
        })}
        </div>
          <ImpressumPage />
        </main>
        <Footer changePage={this.handlePageChange} />
      </div>
    );
  }
}

export default ContractManager;
