import React from "react";
import "./styles/AddForm.css";
import "./styles/BuildControls.css";
import { Link } from "react-router-dom";
import axios from "axios";

class NewCategory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: ""
    };
  }

  handleNameChange = e => {
    this.setState({ name: e.target.value });
  };

  handleAddSubmit = e => {
    e.preventDefault();
    const obj = {
      name: this.state.name
    };
    let url = "http://localhost:9000/contracts/insertCategory";
    axios
      .post(url, obj)
      .then(res => console.log(res.data))
      .then(
        this.setState({
          name: ""
        })
      );
    alert("Sie haben eine neue Kategorie angelegt.");
    window.location.reload();
  };

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleAddSubmit}>
          <h3>Erstellen Sie eine neue Kategorie</h3>
          <label>Name:</label>
          <input
            className="inputAdd"
            type="text"
            placeholder="..."
            name="name"
            value={this.state.name}
            onChange={this.handleNameChange}
            required
          ></input>
          <input className="button4" type="submit" value="Erstellen" />
          <Link to="/home">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}
export default NewCategory;
