import React from "react";
import Bill from "../Bill";
import "../styles/Contracts.css";

class BillsByCategory extends React.Component {
  id = this.props.catId;
  
  componentWillReceiveProps() {
    this.id= this.props.catId;
  }

  render() {
    console.log(this.id);
    return (
      <div className="contractContainer">
        {this.props.bills.map(bill => {
          if (
            bill.catId1 === 2 ||
            bill.catId2 === 2 ||
            bill.catId2 === 2
          ) {
            return (
              <Bill
                name={bill.name}
                id={bill.id}
                price={bill.price}
                description={bill.beschreibung}
                catId1={bill.catId1}
                catId2={bill.catId2}
                catId3={bill.catId3}
                categories={this.props.categories}
              />
            );
          }else {
            return null;
          }
        })}
      </div>
    );
  }
}

export default BillsByCategory;
