import React from "react";
import "./styles/AddForm.css";
import "./styles/BuildControls.css";
import { Link } from "react-router-dom";
import axios from "axios";

class NewContract extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      endDate: "",
      price: "",
      description: "",
      handleNameChange: function(e) {
        this.setState({ name: e.target.value });
      },
      handleEndDateChange: function(e) {
        this.setState({ endDate: e.target.value });
      },
      handlePriceChange: function(e) {
        this.setState({ price: e.target.value });
      },
      handleDescriptionChange: function(e) {
        this.setState({ description: e.target.value });
      },
      handleAddSubmit: function(e) {
        e.preventDefault();
        const obj = {
          name: this.state.name,
          kosten: this.state.price,
          laufzeitBis: this.state.endDate,
          beschreibung: this.state.description
        };
        axios
          .post("", obj)
          .then(res => console.log(res.data))
          .then(this.setState({ name: "", price: "", description: "" }));
      }
    };
  }

  render() {
    return (
      <div className="newContainer">
        <form className="newForm">
          <h3>Erstellen Sie einen neuen Vertrag</h3>
          <label>Name:</label>
          <input
            className="inputAdd"
            type="text"
            placeholder="..."
            name="name"
            onChange={this.handleNameChange}
          ></input>
          <label>Ende:</label>
          <input
            className="inputAdd"
            type="date"
            placeholder="..."
            name="endDate"
            onChange={this.handleEndDateChange}
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            placeholder="..."
            name="price"
            onChange={this.handlePriceChange}
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            placeholder="..."
            name="description"
            onChange={this.handleDescriptionChange}
          ></input>

          <button className="button4" >
            Erstellen
          </button>

          <Link to="/contracts">
            {" "}
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default NewContract;
