import React from "react";
import "./styles/Home.css";

class FilterCategories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      contracts: [],
      selectId: 1
    };
  }

  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }
  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
  }

  filterSelectHandler = () => {
    this.setState({ selectId: e.target.value });
  };

  render() {
    return (
      <div className="HomeContainer">
        <div>
          <select onChange={this.filterSelectHandler} name="Sortieren nach">
            {this.state.categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
      </div>
    );
  }
}

export default FilterCategories;
