import React from "react";
import Bill from "../Bill";
import "../styles/Contracts.css";

class BillsByCategory extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      id: ""
    };
  }

  fetchBillsByCategory() {
    fetch(`/bills/category/${this.props.catId}`)
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }

  componentDidMount() {
    this.fetchBillsByCategory();
  }
  render() {
    console.log(this.id);
    return (
      <div className="contractContainer">
        {this.state.bills.map(bill => {
          if (bill.catId1 === this.props.catId || bill.catId2 === this.props.catId || bill.catId2 === this.props.catId) {
            return (
              <Bill
                key={bill.id}
                name={bill.name}
                id={bill.id}
                price={bill.price}
                description={bill.beschreibung}
                catId1={bill.catId1}
                catId2={bill.catId2}
                catId3={bill.catId3}
                categories={this.props.categories}
              />
            );
          } else {
            return null;
          }
        })}
      </div>
    );
  }
}

export default BillsByCategory;
