import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
class EditBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: {},
      newBill: this.state.bills
    };
  }

  handlePriceChange = e => {
    this.setState({  newBill: { kosten: e.target.value } });
  };

  handleDescriptionChange = e => {
    this.setState({ newBill: { beschreibung: e.target.value } });
  };

  handleNameChange = e => {
    this.setState({  newBill: { name: e.target.value } });
  };

  handleCategory1Change = e => {
    this.setState({  newBill: { catId1: e.target.value } });
  };
  handleCategory2Change = e => {
    this.setState({  newBill: { catId2: e.target.value } });
  };
  handleCategory3Change = e => {
    this.setState({  newBill: { catId3: e.target.value } });
  };

  fetchBills() {
    fetch(`/bills/${this.props.id}`)
      .then(res => res.json())
      .then(bill => this.setState({ bills: bill }))
      .then(err => err);
  }

  componentWillMount() {
    this.fetchBills();
  }
  handleEditSubmit = () => {
    let url = `/bills/updateBill`;
    axios
      .put(url, this.state.newBill, {
        headers: {
          "Content-type": "application/json"
        }
      })
      .then(response => response.json())
      .then(data1 => console.log(data1))
      .catch(err => console.log(err));

    alert("Die Rechnung wurde aktualisiert.");
  };

  render() {
    return (
      <div className="newContainer">
        <form className="newContainer" onSubmit={this.handleEditSubmit}>
          <h3>
            Hier sind Ihre Rechnungsdaten.<br></br> Sie können die Rechnung
            jetzt bearbeiten.
            <br></br>
            <br></br>
            Rechnungs-ID: {this.state.bills.id}
          </h3>
          <label>Name: </label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            value={this.state.newBill.name}
            onChange={this.handleNameChange}
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            value={this.state.newBill.kosten}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            value={this.state.newBill.beschreibung}
            onChange={this.handleDescriptionChange}
            required
          ></input>
          <label>Kategorie: </label>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="category1"
            value={this.state.newBill.catId1}
            onChange={this.handleCategory1Change}
          >
            {this.props.categories.map(category => {
              return (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              );
            })}

          </select>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="category2"
            value={this.state.newBill.catId2}
            onChange={this.handleCategory2Change}
          >
            {this.props.categories.map(category => {
              return (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              );
            })}
            
          </select>
          <select
            className="inputAdd"
            type="text"
            placeholder="..."
            name="category3"
            value={this.state.newBill.catId3}
            onChange={this.handleCategory3Change}
          >
            {this.props.categories.map(category => {
              return (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              );
            })}
            
          </select>
          <input
            className="button4"
            type="submit"
            value="Bearbeitung bestätigen"
          />
        </form>

        <button onClick={() => window.location.reload()} className="button4">
          Abbrechen
        </button>
      </div>
    );
  }
}

export default EditBill;
