import React from "react";
import "./styles/ContentContainer.css";
import "./styles/Forms.css";
import "./styles/GridLayout.css";
import "./styles/Profile.css";
import HomePage from "./Pages/HomePage";
import ContractPage from "./Pages/ContractPage";
import BillPage from "./Pages/BillPage";
import CategoryPage from "./Pages/CategoryPage";
import ProfilePage from "./Pages/ProfilePage";
import { Route, Switch} from "react-router-dom";


class ContentContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loggedIn: true,
      userId: "",
      title: this.props.title,
      currentPage: this.props.currentPage,
      content: "",
      user: this.props.user,
      contracts: [],
      bills: [],
      categories: []
    };
  }

 


  render() {
    return (
      <div>
        <Switch>
          <Route exact path="/" component={HomePage}></Route>
          <Route path="/contracts" component={BillPage}/>
          
        </Switch>
        {this.state.currentPage === 0 && <HomePage title="Home" contracts={this.state.contracts} bills={this.state.bills} />}
        {this.state.currentPage === 1  && <BillPage title="Bills" bills={this.state.bills} />}
        {this.state.currentPage === 2  && <ContractPage title="VertrÃ¤ge" contracts={this.state.contracts} />}
        {this.state.currentPage === 3  && <CategoryPage title="Kategorien" />}
        {this.state.currentPage === 4  && <ProfilePage title="Ihr Profil" userData={this.state.user} editUserData={this.props.editUserData}/>}
      </div>
    );
  }
}

export default ContentContainer;
