import React from "react";
import "./styles/Profile.css";

class profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  handleInput = props => {};

  handleEdit = (type, placeholderText) => {
    return (
      <div>
        <label>
          <input
            onChange={this.handleInput}
            type={type}
            placeholder={placeholderText}
          />
        </label>
      </div>
    );
  };

  render() {
    return (
      <div className="profile">
        <section className="profileSection">
          <ul>
            <li>
              {" "}
              <div className="pDiv">
                <div>Ihre User ID:</div>
                <div className="innerDiv">{this.props.user.id}</div>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Username:</div>
                <div className="innerDiv">{this.props.user.username}</div>
                <button
                  onClick={this.handleEdit("text", "Ihr neuer Username")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Name:</div>
                <div className="innerDiv">{this.props.user.name}</div>
                <button
                  onClick={this.handleEdit("text", "Ihr neuer Name:")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>Vorname:</div>
                <div className="innerDiv">{this.props.user.firstName}</div>
                <button
                  onClick={this.handleEdit("text", "Ihr neuer Vorname")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>E-Mail:</div>
                <div className="innerDiv">{this.props.user.email}</div>
                <button
                  onClick={this.handleEdit("text", "Ihre neue email")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>angemeldet seit:</div>
                <div className="innerDiv">{this.props.user.signUpDate}</div>
              </div>
            </li>
          </ul>
        </section>
      </div>
    );
  }
}

export default profile;
