import React from "react";
import "./styles/Profile.css";

class profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        user: this.props.user

    };
  }
  handleInput = props => {
      this.props.editUserData();
  };

  handleEdit = (type, placeholderText, key) => {
    return (
      <div>
        <label>
          <input
            onChange={this.handleInput(key)}
            type={type}
            placeholder={placeholderText}
          />
        </label>
      </div>
    );
  };

  render() {
    return (
      <div className="profile">
        <section className="profileSection">
          <ul>
            <li>
              {" "}
              <div className="pDiv">
                <div>Ihre User ID:</div>
                <div className="innerDiv">{this.props.user.id}</div>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Username:</div>
                <div className="innerDiv">{this.props.user.username}</div>
                <button
                  onClick={this.handleEdit("text", "Ihr neuer Username", "username")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              {" "}
              <div className="pDiv">
                <div>Name:</div>
                <div className="innerDiv">{this.props.user.name}</div>
                <button
                  onClick={this.handleEdit("text", "Ihr neuer Name:", "name")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>Vorname:</div>
                <div className="innerDiv">{this.props.user.firstName}</div>
                <button
                  onClick={this.handleEdit("text", "Ihr neuer Vorname", "firstName")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>E-Mail:</div>
                <div className="innerDiv">{this.props.user.email}</div>
                <button
                  onClick={this.handleEdit("text", "Ihre neue email", "email")}
                  className="button3"
                >
                  Edit
                </button>
              </div>
            </li>
            <li>
              <div className="pDiv">
                <div>angemeldet seit:</div>
                <div className="innerDiv">{this.props.user.signUpDate}</div>
              </div>
            </li>
          </ul>
        </section>
      </div>
    );
  }
}

export default profile;
