import React from "react";
import BillsByCategory from "./BillsByCategory";

import "../styles/Home.css";
import "../styles/AddForm.css";
import "../styles/Contracts.css";

class FilterCategories extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      newBills: [],
      contracts: [],
      selectId: ""
    };
  }

  fetchContracts() {
    fetch("/contracts")
      .then(res => res.json())
      .then(contracts => this.setState({ contracts: contracts }))
      .then(err => err);
  }

  fetchBills() {
    fetch("/bills")
      .then(res => res.json())
      .then(bills => this.setState({ bills: bills }))
      .then(err => err);
  }
  componentDidMount() {
    this.fetchContracts();
    this.fetchBills();
  }
  handleButtonClick = e => {
    let newB = [];
    this.state.bills.map(bill => {
      if (
        bill.catId1 === this.state.selectId ||
        bill.catId2 === this.state.selectId ||
        bill.catId3 === this.state.selectId
      ) {
        newB.push(bill);
      }
    });
    this.setState({ newBills: newB });
  };

  filterSelectHandler = e => {
    this.setState({ selectId: e.target.value });
  };

  render() {
    return (
      <div className="content-container">
        <div classNamae="content-container">
          <label className="labelCat">Nach welcher Kategorie filtern? </label>
          <form className="BuildControls">
            <select
              className="inputAdd"
              onChange={this.filterSelectHandler}
              name="Sortieren nach"
            >
              {this.props.categories.map(category => {
                if (category.id === 1) {
                  return (
                    <option key={category.id} value={category.id}>
                      ohne Kategorie
                    </option>
                  );
                } else {
                  return (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  );
                }
              })}
            </select>
            <button className="button4" onClick={this.handleButtonClick}>
              Go
            </button>
          </form>
        </div>
        <div>
          <BillsByCategory
            catId={this.state.selectId}
            bills={this.state.newBills}
            categories={this.props.categories}
          />
        </div>
      </div>
    );
  }
}

export default FilterCategories;
