import React from "react";
import "../styles/ContentContainer.css";
import "../styles/AddForm.css";
import "../styles/BuildControls.css";
import "../styles/Auth.css";
import axios from "axios";

class AuthContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      user: {},
      loggedIn: false
    };
  }

  handleMailChange = e => {
    this.setState({ email: e.target.value });
  };

  handlePasswordChange = e => {
    this.setState({ password: e.target.value });
  };

  loginUser = () => {
    let url = "http://localhost:9000/bills/insertBill";
    axios
      .post(url)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Eine Neue Rechnung wurde angelegt.");
    window.location.reload();
  };

  render() {
    return (
      <div className="authContainer">
        <div>
          <h3> </h3>
          <h3> </h3>
          <h1>Willkommen bei fily - Rechnungsmanager </h1>
        </div>
        <h2>Melden Sie sich an:</h2>
        <div className="authFormDiv">
          <form className="authForm">
            <label>E-Mail: </label>
            <input
              className="authInput"
              type="text"
              onChange={this.handleMailChange}
              required
            ></input>
            <label>Passwort: </label>
            <input
              className="authInput"
              type="text"
              onChange={this.handlePasswordChange}
              required
            ></input>
          </form>
          <div className="authButtonDiv">
            <button className="buttonA" onClick={this.loginUser}>
              Login
            </button>
            <button className="buttonA" onClick={this.registerUser}>
              Registrieren
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default AuthContainer;
