import React from "react";
import { Link } from "react-router-dom";

let countBills = 0;
let countContracts = 0;

const countBillCategory = (categories, data) => {
  let counter = 0;
  categories.map(category => {
    data.map(d => {
      if (
        d.catId1 === category.id ||
        d.catId2 === category.id ||
        d.catId3 === category.id
      ) {
        counter = counter + 1;
      }
    });
  });
  countBills = counter;
};

const countContractCategory = (categories, data) => {
  let counter = 0;
  categories.map(category => {
    data.map(d => {
      if (
        d.catId1 === category.id ||
        d.catId2 === category.id ||
        d.catId3 === category.id
      ) {
        counter = counter + 1;
      }
    });
    countContracts = counter;
    counter = 0;
  });
};

const sumCategories = props => {
  countBillCategory(props.categories, props.bills);
  countContractCategory(props.categories, props.contracts);
  let countAll = countBills + countContracts;
  return (
    <div className="sumObj">
      <Link to="/categories">
        <div>
          <h3>Kategorien</h3>
          <ul className="HomeList">
            {props.categories.map(category => {
              if (category.id === 1) {
                return <li key={category.id}>ohne Kategorie</li>;
              } else {
                return (
                  <li key={category.id}>
                    {category.name}
                    {countAll}
                  </li>
                );
              }
            })}
          </ul>
        </div>
      </Link>
    </div>
  );
};

export default sumCategories;
