import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class EditBill extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      billId: this.props.location.state
    };
  }

  handleEditSubmit = e => {
    e.preventDefault();
    let obj = this.state.bills;
    let url = "http://localhost9000/bills/updateBill";
    axios
      .put(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Die Rechnung wurde aktualisiert.");
    window.location.reload();
  };

  handlePriceChange = e => {
    this.setState({ price: e.target.value });
  };

  handleDescriptionChange = e => {
    this.setState({ beschreibung: e.target.value });
  };

  handleNameChange = e => {
    this.setState({ name: e.target.value });
  };

  fetchBills() {
    fetch(`/bills/24`)
      .then(res => res.json())
      .then(bill => this.setState({ bills: bill }))
      .then(err => err);
  }

  componentDidMount() {
    this.fetchBills();
  }

  render() {
    return (
      <div className="newContainer">
        <form className="newForm" onSubmit={this.handleEditSubmit}>
          <h3>
            Hier sind Ihre Rechnungsdaten.<br></br> Sie k√∂nnen die Rechnung
            jetzt bearbeiten.
            <br></br>
            <br></br>
            Rechnungs-ID: {this.state.bills.id}
          </h3>
          <label>Name: </label>
          <input
            className="inputAdd"
            type="text"
            name="name"
            value={this.state.bills.name}
            onChange={this.handleNameChange}
          ></input>
          <label>Preis:</label>
          <input
            className="inputAdd"
            type="number"
            name="price"
            value={this.state.bills.kosten}
            onChange={this.handlePriceChange}
            required
          ></input>
          <label>Beschreibung:</label>
          <input
            className="inputAdd"
            type="text"
            name="description"
            value={this.state.bills.beschreibung}
            onChange={this.handleDescriptionChange}
            required
          ></input>
          <input
            className="button4"
            type="submit"
            value="Bearbeitung best√§tigen"
          />
          <Link to="/bills">
            <button className="button4">Abbrechen</button>
          </Link>
        </form>
      </div>
    );
  }
}

export default EditBill;
