import React from "react";
import { Link } from "react-router-dom";

const countCategory = (categories, data) => {
  let count = 0;
  categories.map(category => {
    data.map(d => {
      if (
        d.catId1 === category.id ||
        d.catId2 === category.id ||
        d.catId3 === category.id
      ) {
        return (count = count + 1);
      }
    });
  });
};

const sumCategories = props => {
  let count;
  return (
    <div className="sumObj">
      <Link to="/categories">
        <div>
          <h3>Kategorien</h3>
          <ul className="HomeList">
            {props.categories.map(category => {
              if (category.id === 1) {
                return <li key={category.id}>ohne Kategorie</li>;
              } else {
                return <li key={category.id}>{category.name}</li>;
              }
            })}
          </ul>
        </div>
      </Link>
    </div>
  );
};

export default sumCategories;
