import React from "react";
import "../styles/Auth.css";
import "../styles/GridLayout.css";
import axios from "axios";

class RegisterForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      name: "",
      firstName: "",
      email: "",
      password: ""
    };
  }
  handleUsernameChange = e => {
    this.setState({ username: e.target.value });
  };

  handleNameChange = e => {
    this.setState({ namee: e.target.value });
  };

  handleVornameChange = e => {
    this.setState({ firstName: e.target.value });
  };

  handleMailChange = e => {
    this.setState({ email: e.target.value });
  };

  handlePasswordChange = e => {
    this.setState({ password: e.target.value });
  };
  handleAddSubmit = e => {
    e.preventDefault();
    let obj = {
      name: this.state.name,
      userId: 1,
      kosten: this.state.price,
      beschreibung: this.state.description,
      catId1: this.state.catId1,
      catId2: this.state.catId2,
      catId3: this.state.catId3
    };

    let url = "http://localhost:9000/bills/insertBill";
    axios
      .post(url, obj)
      .then(res => console.log(res))
      .catch(e => console.log(e));

    alert("Eine Neue Rechnung wurde angelegt.");
    window.location.reload();
  };

  render() {
    return (
      <div className="authFormDiv">
        <h2>Registrieren Sie sich:</h2>
        <form className="authForm">
          <label>Username: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleUsernameChange}
            required
          ></input>
          <label>Name: </label>
          <input
            className="authInput"
            type="password"
            onChange={this.handleNameChange}
            required
          ></input>
          <label>Vorname: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleVornameChange}
            required
          ></input>
          <label>E-Mail: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handleMailChange}
            required
          ></input>
          <label>Passwort: </label>
          <input
            className="authInput"
            type="text"
            onChange={this.handlePasswordChange}
            required
          ></input>
          <label>Anmeldedatum: </label>
          <input
            className="authInput"
            type="test"
            value={new Date()
              .toISOString()
              .slice(0, 19)
              .replace("T", " ")}
            readonly
          ></input>
        </form>
        <div className="authButtonDiv">
          <button className="buttonA" onClick={this.registerUser}>
            Registrieren
          </button>
          <button className="buttonA" onClick={() => window.location.reload()}>
            Abbrechen
          </button>
        </div>
      </div>
    );
  }
}

export default RegisterForm;
