import React from "react";
import "./styles/GridLayout.css";
import "./styles/FilterSelect.css";

const filterSelectHandler =(props) => {
  let optionValue;
  let mySelect = document.getElementById("selector");
  optionValue = mySelect.options[mySelect.selectedIndex].value;
    
}

const filterPrice = props => (
  <div>
    <input type="number" title="Min" />
    <input type="number" title="Max" />
  </div>
);

const filterDate = props => (
  <div>
    <input type="date" title="Min" />
    <input type="date" title="Max" />
  </div>
);

const filterSelect = props => {
  return (
    <div className="topBar col-11">
      <label> Sortieren nach: </label>
      <select onChange={filterSelectHandler} name="Sortieren nach" id="selector">
        <option value="0"> (nichts ausgewÃ¤hlt)</option>
        <option value="1"> Datum (aufsteigend)</option>
        <option value="2"> Datum (absteigend)</option>
        <option value="3"> Kosten (aufsteigend)</option>
        <option value="4"> Kosten (absteigend)</option>
        <option value="5"> Name (aufsteigend)</option>
        <option value="6"> Name (absteigend)</option>
      </select>
    </div>
  );
};

export default filterSelect;
